const [show, setShow] = useState(false);

const [count, setCount] = useState(0);
const [cartitems,setCartitems] = useState([]);

const onAdd = (book)=>{
  const exist = cartitems.find(x =>x.isbn===book.isbn);
  if(exist){
    setCartitems(cartitems.map(x=>x.isbn===book.isbn ?{...exist,qty:exist.qty+1} : x))

  }
  else{
    setCartitems([...cartitems,{...book,qty:1}])
  }
}

const onRemove=(book)=>{

  const exist = cartitems.find((x)=>x.isbn===book.isbn);
  if(exist.qty===1){
    setCartitems(cartitems.filter((x=>x.isbn !==book.isbn)));
  }
  else{
    setCartitems(cartitems.map(x=>x.isbn===book.isbn ?{...exist,qty:exist.qty-1}:x))

  }
}


